mapper.select:
select
detail, projectID, projectName, partnerID, partnerMerchantID, partnerShopID, partnerOrderID, partnerPaymentID,
sourceOfFundType, sourceOfFundMerchantID, sourceOfFundShopID, Amount, currencyCode, paymentStatus,
createDateTime, paymentDateTime, partnerPaymentDate, paymentProcessingDate, cashID, partnerSOF, cardBrand,
issuerBank, cardMasking, transactionAccount, approvalCode, buyerName, paymentFeeAbsorber,
paymentFeeCollectionType, paymentFeeAmount, paymentVATAmount, paymentFeeNetAmount, paymentWHTAmount,
feeCurrencyCode, reconcileFlag, refundFlag, payoutStatus, payerSourceOfFund, id, is_processed, created_at,
update_at
from tb_kgp_payment_detail


        and detail = #{detail}


        and projectID = #{projectid}


        and projectName = #{projectname}


        and partnerID = #{partnerid}


        and partnerMerchantID = #{partnermerchantid}


        and partnerShopID = #{partnershopid}


        and partnerOrderID = #{partnerorderid}


        and partnerPaymentID = #{partnerpaymentid}


        and sourceOfFundType = #{sourceoffundtype}


        and sourceOfFundMerchantID = #{sourceoffundmerchantid}


        and sourceOfFundShopID = #{sourceoffundshopid}


        and Amount = #{amount}


        and currencyCode = #{currencycode}


        and paymentStatus = #{paymentstatus}


        and createDateTime = #{createdatetime}


        and paymentDateTime = #{paymentdatetime}


        and partnerPaymentDate = #{partnerpaymentdate}


        and paymentProcessingDate = #{paymentprocessingdate}


        and cashID = #{cashid}


        and partnerSOF = #{partnersof}


        and cardBrand = #{cardbrand}


        and issuerBank = #{issuerbank}


        and cardMasking = #{cardmasking}


        and transactionAccount = #{transactionaccount}


        and approvalCode = #{approvalcode}


        and buyerName = #{buyername}


        and paymentFeeAbsorber = #{paymentfeeabsorber}


        and paymentFeeCollectionType = #{paymentfeecollectiontype}


        and paymentFeeAmount = #{paymentfeeamount}


        and paymentVATAmount = #{paymentvatamount}


        and paymentFeeNetAmount = #{paymentfeenetamount}


        and paymentWHTAmount = #{paymentwhtamount}


        and feeCurrencyCode = #{feecurrencycode}


        and reconcileFlag = #{reconcileflag}


        and refundFlag = #{refundflag}


        and payoutStatus = #{payoutstatus}


        and payerSourceOfFund = #{payersourceoffund}


        and id = #{id}


        and is_processed = #{isProcessed}


        and created_at = #{createdAt}


        and update_at = #{updateAt}


limit #{pageable.offset}, #{pageable.pageSize}

