mapper.select:
select
detail, receiverType, receiverID, projectID, partnerID, partnerMerchantID, partnerShopID, partnerOrderID,
partnerPaymentID, sourceOfFundType, sourceOfFundMerchantID, sourceOfFundShopID, paymentAmount,
paymentFeeAbsorber, paymentFeeCollectionType, paymentFeeAmount, paymentVATAmount, paymentFeeNetAmount,
paymentWHTAmount, currencyCode, paymentDateTime, paymentProcessingDate, payoutType, payoutLevel,
mainBatchIDType, mainBatchID, distributionAmount, accountNumber, bankName, payoutStatus, errorName,
createdDateTime, transferDateTime, transferSuccessDateTime, completedDateTime, partnerBatchID, batchID,
partnerPayoutMerchantID, payoutMerchantID, payoutPartnerBatchID, payerSourceOfFund, id, is_processed,
created_at, update_at
from tb_kgp_settle_detail


        and detail = #{detail}


        and receiverType = #{receivertype}


        and receiverID = #{receiverid}


        and projectID = #{projectid}


        and partnerID = #{partnerid}


        and partnerMerchantID = #{partnermerchantid}


        and partnerShopID = #{partnershopid}


        and partnerOrderID = #{partnerorderid}


        and partnerPaymentID = #{partnerpaymentid}


        and sourceOfFundType = #{sourceoffundtype}


        and sourceOfFundMerchantID = #{sourceoffundmerchantid}


        and sourceOfFundShopID = #{sourceoffundshopid}


        and paymentAmount = #{paymentamount}


        and paymentFeeAbsorber = #{paymentfeeabsorber}


        and paymentFeeCollectionType = #{paymentfeecollectiontype}


        and paymentFeeAmount = #{paymentfeeamount}


        and paymentVATAmount = #{paymentvatamount}


        and paymentFeeNetAmount = #{paymentfeenetamount}


        and paymentWHTAmount = #{paymentwhtamount}


        and currencyCode = #{currencycode}


        and paymentDateTime = #{paymentdatetime}


        and paymentProcessingDate = #{paymentprocessingdate}


        and payoutType = #{payouttype}


        and payoutLevel = #{payoutlevel}


        and mainBatchIDType = #{mainbatchidtype}


        and mainBatchID = #{mainbatchid}


        and distributionAmount = #{distributionamount}


        and accountNumber = #{accountnumber}


        and bankName = #{bankname}


        and payoutStatus = #{payoutstatus}


        and errorName = #{errorname}


        and createdDateTime = #{createddatetime}


        and transferDateTime = #{transferdatetime}


        and transferSuccessDateTime = #{transfersuccessdatetime}


        and completedDateTime = #{completeddatetime}


        and partnerBatchID = #{partnerbatchid}


        and batchID = #{batchid}


        and partnerPayoutMerchantID = #{partnerpayoutmerchantid}


        and payoutMerchantID = #{payoutmerchantid}


        and payoutPartnerBatchID = #{payoutpartnerbatchid}


        and payerSourceOfFund = #{payersourceoffund}


        and id = #{id}


        and is_processed = #{isProcessed}


        and created_at = #{createdAt}


        and update_at = #{updateAt}


limit #{pageable.offset}, #{pageable.pageSize}

